import React from "react";
import "../styles/dashboard.css";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Tooltip,
  Legend,
} from "chart.js";

import { Line, Bar } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Tooltip,
  Legend
);

function Dashboard() {
  const lineData = {
    labels: ["Mon", "Tue", "Wed", "Thu", "Fri"],
    datasets: [
      {
        label: "Weekly Sales",
        data: [5, 12, 8, 20, 15],
        borderColor: "#2563eb",
        backgroundColor: "#93c5fd",
        tension: 0.4,
      },
    ],
  };

  const barData = {
    labels: ["Products", "Orders"],
    datasets: [
      {
        label: "Count",
        data: [12, 0],
        backgroundColor: ["#60a5fa", "#bfdbfe"],
      },
    ],
  };

  return (
    <div className="admin-container">
      {/* LEFT SIDEBAR */}
      <aside className="sidebar">
        <h2>BuyBlink Admin</h2>
        <ul>
          <li>Dashboard</li>
          <li>Add Product</li>
          <li>Products List</li>
        </ul>
      </aside>

      {/* RIGHT CONTENT */}
      <main className="dashboard-content">
        <div className="chart-card">
          <Line data={lineData} />
        </div>

        <div className="chart-card">
          <Bar data={barData} />
        </div>
      </main>
    </div>
  );
}

export default Dashboard;
