import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function EditProduct() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    price: "",
    category: "",
    image: "",
    description: ""
  });

  useEffect(() => {
    fetch(`http://localhost:5000/api/products/${id}`)
      .then(res => res.json())
      .then(data => setForm(data.product));
  }, [id]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    const res = await fetch(`http://localhost:5000/api/products/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    });

    const data = await res.json();
    if (data.success) {
      alert("Product Updated");
      navigate("/admin/products");
    }
  };

  return (
    <div>
      <h2>Edit Product</h2>

      <form onSubmit={handleUpdate}>
        <input name="name" value={form.name}  placeholder="Name" onChange={handleChange} />
        <input name="price" value={form.price}   placeholder="Name" onChange={handleChange} />
        <input name="category" value={form.category}  placeholder=""  onChange={handleChange} />
        <input name="image" value={form.image}  placeholder="image"  onChange={handleChange} />
        <textarea name="description" value={form.description}  placeholder="discription"  onChange={handleChange} />

        <button type="submit">Update</button>
      </form>
    </div>
  );
}
