import React, { useState } from "react";

export default function AddProduct() {
  const [form, setForm] = useState({
    id: "",        // ðŸ‘ˆ product id (for edit/delete)
    name: "",
    price: "",
    category: "",
    image: "",
    description: ""
  });

  // handle input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // ADD product
  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://localhost:5000/api/products", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    });

    const data = await response.json();
    alert(data.success ? "Product added!" : "Error");
  };

  // EDIT product
  const handleEdit = async () => {
    if (!form.id) return alert("Enter Product ID");

    const response = await fetch(
      `http://localhost:5000/api/products/${form.id}`,
      {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      }
    );

    const data = await response.json();
    alert(data.success ? "Product updated!" : "Error updating");
  };

  // DELETE product
  const handleDelete = async () => {
    if (!form.id) return alert("Enter Product ID");

    const response = await fetch(
      `http://localhost:5000/api/products/${form.id}`,
      { method: "DELETE" }
    );

    const data = await response.json();
    alert(data.success ? "Product deleted!" : "Error deleting");
  };

  return (
    <div>
      <h1>Add / Edit Product</h1>

      <input
        name="id"
        placeholder="Product ID (for edit/delete)"
        onChange={handleChange}
      />

      <input name="name" placeholder="Product Name" onChange={handleChange} />

      <input
        name="price"
        type="number"
        placeholder="Price"
        onChange={handleChange}
      />

      <input name="category" placeholder="Category" onChange={handleChange} />

      <input name="image" placeholder="Image URL" onChange={handleChange} />

      <textarea
        name="description"
        placeholder="Description"
        onChange={handleChange}
      />

      <br /><br />

      <button onClick={handleSubmit}>Add Product</button>
      <button onClick={handleEdit}>Edit Product</button>
      <button onClick={handleDelete}>Delete Product</button>
    </div>
  );
}
