import React from "react";
import "../com/Hero.css";

const Hero = ({ onShopNow }) => {
  return (
    <div className="hero-section">
      <div className="hero-content">
        <h1>Welcome to BuyBlink</h1>
        <p>Shop the latest collections & trending products</p>
        <button className="shop-btn" onClick={onShopNow}>Shop Now</button>
      </div>

      <img className="hero-image" src="/images/hero-banner.png" alt="hero" />
    </div>
  );
};

export default Hero;
