import React, { useState, useEffect } from "react";
import { getCart, removeFromCart } from "../utils/cart";

export default function Cart() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    setItems(getCart());
  }, []);

  const removeItem = (id) => {
    removeFromCart(id);
    setItems(getCart());
  };

  const totalCost = items.reduce(
    (sum, item) => sum + Number(item.price),
    0
  );

  const buyProducts = () => {
    if (items.length === 0) {
      alert("Cart is empty");
      return;
    }
    alert(`Proceeding to buy products worth ₹${totalCost}`);
  };

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>

      {items.length === 0 && <p>Cart is empty</p>}

      {items.map((item) => (
        <div key={item._id || item.name} className="cart-item">
          <img src={item.image} alt={item.name} width="80" />
          <div>
            <h4>{item.name}</h4>
            <p>₹{item.price}</p>
          </div>

          <button onClick={() => removeItem(item._id || item.name)}>
            Remove
          </button>
        </div>
      ))}

      {items.length > 0 && (
        <>
          <hr />
          <h3>Total: ₹{totalCost}</h3>

          <button onClick={buyProducts}>
            Buy Products
          </button>
        </>
      )}
    </div>
  );
}
