import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import ProductCard from "../components/ProductCard";
import CategoryBar from "../components/CategoryBar";
import "../styles/product.css";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [category, setCategory] = useState("all");

  const location = useLocation();

  /* ✅ URL se category read karna (?category=men) */
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const cat = params.get("category");

    if (cat) {
      setCategory(cat.toLowerCase());
    } else {
      setCategory("all");
    }
  }, [location.search]);

  /* ✅ Products fetch */
  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch(() => setProducts([]));
  }, []);

  /* ✅ SMART FILTER (men / mens / MEN sab chalega) */
  const filteredProducts =
    category === "all"
      ? products
      : products.filter((p) => {
          if (!p.category) return false;

          const productCat = p.category.toLowerCase();

          if (category === "mens") return productCat.includes("men");
          if (category === "women") return productCat.includes("women");
          if (category === "kids") return productCat.includes("kid");

          return false;
        });

  return (
    <div className="product-page">
      <CategoryBar setCategory={setCategory} />

      <div className="product-grid">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((p) => (
            <ProductCard key={p._id} product={p} />
          ))
        ) : (
          <p className="no-products">No products found</p>
        )}
      </div>
    </div>
  );
}
