import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { addToCart } from "../utils/cart";
import "../styles/productDetail.css";

export default function ProductDetail() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [selectedColor, setSelectedColor] = useState("");

  useEffect(() => {
    fetch(`http://localhost:5000/api/products/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setProduct(data);
        if (data.colors && data.colors.length > 0) {
          setSelectedColor(data.colors[0]);
        }
      });
  }, [id]);

  if (!product) return <h2 style={{ padding: "40px" }}>Loading...</h2>;

  const buyNow = () => {
    alert(`Buying: ${product.name}`);
  };

  return (
    <div className="product-detail-page">
      
      {/* LEFT IMAGE */}
      <div className="image-section">
        <img src={product.image} alt={product.name} />
      </div>

      {/* RIGHT INFO */}
      <div className="info-section">
        <h1>{product.name}</h1>
        <h2 className="price">‚Çπ{product.price}</h2>

        <p className="description">{product.description}</p>

        {/* COLORS */}
        {product.colors && (
          <div className="colors">
            <p>Available Colors</p>
            <div className="color-list">
              {product.colors.map((color, i) => (
                <span
                  key={i}
                  className={`color-circle ${
                    selectedColor === color ? "active" : ""
                  }`}
                  style={{ backgroundColor: color }}
                  onClick={() => setSelectedColor(color)}
                />
              ))}
            </div>
          </div>
        )}

        {/* DELIVERY */}
        <p className="delivery">
          üöö Delivery by <b>{product.deliveryDate || "3‚Äì5 Working Days"}</b>
        </p>

        {/* STOCK */}
        <p className="stock">
          {product.stock > 0 ? "‚úÖ In Stock" : "‚ùå Out of Stock"}
        </p>

        {/* ACTION BUTTONS */}
        <div className="buttons">
          <button
            className="cart-btn"
            onClick={() => addToCart({ ...product, selectedColor })}
          >
            Add to Cart
          </button>

          <button className="buy-btn" onClick={buyNow}>
            Buy Now
          </button>
        </div>
      </div>
    </div>
  );
}
